mapper.update:
UPDATE 		xo_board 
SET 		board_groupId=#{board_read}
WHERE 		board_num=#{board_read}

UPDATE 		xo_board 
SET 		board_read=board_read+1
WHERE 		board_num=#{num}

UPDATE		xo_board
SET 		board_sub = #{board_sub},
			board_pw = #{board_pw},
			board_text = #{board_text}
WHERE		board_num = #{board_num}
